// Extract the array of URLs
const urls =  $input.first().json.response || [];

// Map URLs into the required structure
const formattedURLs = urls.map(url => ({ "video_url": url }));

// Return the transformed data
return [{
    json: {
        "video_urls": formattedURLs
    }
}];

// Convert SRT timestamp to seconds
const parseTime = (timestamp) => {
  const [h, m, sMs] = timestamp.split(':');
  const [s, ms] = sMs.split(',');
  return (
    parseInt(h) * 3600 +
    parseInt(m) * 60 +
    parseInt(s) +
    parseInt(ms) / 1000
  );
};

// Better sentence splitter (fallback for subtitles without punctuation)
const splitSentences = (text) => {
  // Try splitting on .!? followed by space or line end
  let sentences = text.match(/[^.!?]+[.!?]*/g);

  // If no punctuation, fallback: split by 10 words each
  if (!sentences || sentences.length === 1) {
    const words = text.split(/\s+/);
    sentences = [];
    for (let i = 0; i < words.length; i += 10) {
      sentences.push(words.slice(i, i + 10).join(' '));
    }
  }

  return sentences.slice(0, 3); // Only first 3
};

// Parse SRT from input
const srt = $input.first().json.response.srt;

// Split into subtitle blocks
const blocks = srt.trim().split(/\n\s*\n/);

// Process each subtitle block
const parsedBlocks = blocks.map(block => {
  const lines = block.split('\n');
  if (lines.length < 3) return null;

  const timeLine = lines[1];
  const [start, end] = timeLine.split(' --> ').map(parseTime);
  const duration = parseFloat((end - start).toFixed(3));

  const fullText = lines.slice(2).join(' ').trim();
  const sentences = splitSentences(fullText);
  const slicedText = sentences.join(' ').trim();

  return { text: slicedText, duration };
}).filter(item => item !== null);

// Attach previousText and build result
const result = parsedBlocks.map((item, index, arr) => {
  const previousText = index > 0 ? arr[index - 1].text : "";
  return {
    json: {
      previousText,
      currentText: item.text,
      duration: item.duration
    }
  };
});

return result;
